export var templateStyles = "\n    img {\n        border                 : none;\n        -ms-interpolation-mode : bicubic;\n        max-width              : 100%;\n    }\n\n    body {\n        background-color         : #ffffff;\n        color                    : #000000;\n        font-family              : -apple-system, blinkmacsystemfont, 'segoe ui', roboto, helvetica, arial, sans-serif, 'apple color emoji', 'segoe ui emoji', 'segoe ui symbol';\n        -webkit-font-smoothing   : antialiased;\n        font-size                : 14px;\n        line-height              : 1.4;\n        width                    : 100%;\n        margin                   : 0;\n        padding                  : 0;\n        -ms-text-size-adjust     : 100%;\n        -webkit-text-size-adjust : 100%;\n    }\n\n    /* -------------------------------------\n    BODY & CONTAINER\n    ------------------------------------- */\n\n    .container {\n        display    : block;\n        margin     : 0 auto !important;\n        background : #ffffff;\n        padding    : 20px;\n    }\n\n    .content {\n        box-sizing    : border-box;\n        display       : block;\n        margin        : 0 auto;\n        max-width     : 100%;\n        padding       : 20px;\n        background    : #ffffff;\n        border-radius : 8px;\n    }\n\n    @media(max-width: 768px) {\n        .content {\n            border-radius : 0;\n        }\n    }\n\n    /* -------------------------------------\n    HEADER, FOOTER, MAIN\n    ------------------------------------- */\n\n    .content-block {\n        padding-bottom : 10px;\n        padding-top    : 10px;\n    }\n\n    .footer {\n        clear      : both;\n        margin-top : 10px;\n        padding    : 5px 10px;\n        text-align : center;\n        width      : 100%;\n    }\n    .footer td,\n    .footer p,\n    .footer span,\n    .footer a {\n        color      : #999999;\n        font-size  : 12px;\n        text-align : center;\n    }\n\n    /* -------------------------------------\n    TYPOGRAPHY\n    ------------------------------------- */\n    h1,\n    h2,\n    h3,\n    h4 {\n        color         : #000000;\n        font-family   : sans-serif;\n        font-weight   : 400;\n        line-height   : 1.4;\n        margin        : 0;\n        margin-bottom : 10px;\n    }\n\n    h1 {\n        font-size      : 35px;\n        font-weight    : 300;\n        text-align     : center;\n        text-transform : capitalize;\n    }\n\n    p,\n    ul,\n    ol {\n        font-size     : 14px;\n        font-weight   : normal;\n        margin        : 0;\n        margin-bottom : 15px;\n    }\n\n    li {\n        list-style-position : inside;\n        margin-left         : 5px;\n    }\n\n    a {\n        color           : #2f1c6a !important;\n        text-decoration : none;\n        font-size       : 14px;\n    }\n\n    p,\n    span,\n    div {\n        color     : #000000;\n        font-size : 14px;\n    }\n\n    /* -------------------------------------\n    BUTTONS\n    ------------------------------------- */\n    .btn {\n        box-sizing : border-box;\n        width      : 100%;\n    }\n    .btn > tbody > tr > td {\n        padding-bottom : 15px;\n    }\n    .btn table {\n        width : auto;\n    }\n    .btn table td {\n        background-color : #ffffff;\n        border-radius    : 5px;\n        text-align       : center;\n    }\n    .btn a {\n        background-color : #ffffff;\n        border           : solid 1px #3498db;\n        border-radius    : 5px;\n        box-sizing       : border-box;\n        color            : #3498db;\n        cursor           : pointer;\n        display          : inline-block;\n        font-size        : 14px;\n        font-weight      : bold;\n        margin           : 0;\n        padding          : 12px 25px;\n        text-decoration  : none;\n        text-transform   : capitalize;\n    }\n\n    .btn-primary table td {\n        background-color : #3498db;\n    }\n\n    .btn-primary a {\n        background-color : #3498db;\n        border-color     : #3498db;\n        color            : #ffffff;\n    }\n\n    /* -------------------------------------\n    OTHER STYLES THAT MIGHT BE USEFUL\n    ------------------------------------- */\n\n    .mt0 {\n        margin-top : 0;\n    }\n\n    .mb0 {\n        margin-bottom : 0;\n    }\n\n    .mt5 {\n        margin-top : 5px;\n    }\n\n    .mt10 {\n        margin-top : 10px;\n    }\n\n    .mt20 {\n        margin-top : 20px;\n    }\n\n    .mt30 {\n        margin-top : 30px;\n    }\n\n    .pt10 {\n        padding-top : 10px;\n    }\n\n    .pt20 {\n        padding-top : 20px;\n    }\n\n    .colored {\n        color : #2f1c6a;\n    }\n\n    .bold {\n        font-weight : 600;\n    }\n\n    .border {\n        border : 1px solid #D9DEFF;\n    }\n    .border-top {\n        border-top : 1px solid #D9DEFF;\n    }\n    .border-bottom {\n        border-bottom : 1px solid #D9DEFF;\n    }\n\n    .preheader {\n        color      : transparent;\n        display    : none;\n        height     : 0;\n        max-height : 0;\n        max-width  : 0;\n        opacity    : 0;\n        overflow   : hidden;\n        mso-hide   : all;\n        visibility : hidden;\n        width      : 0;\n    }\n\n    .powered-by a {\n        text-decoration : none;\n    }\n\n    hr {\n        border        : 0;\n        border-bottom : 1px solid #f6f6f6;\n        margin        : 20px 0;\n    }\n\n    .block {\n        display: block\n    }\n\n    .inline-block {\n        display: inline-block\n    }\n\n    .inline {\n        display: inline\n    }\n\n    .hidden {\n        display: none\n    }\n\n    .h-full {\n        height: 100%\n    }\n\n    .w-1/4 {\n        width: 25%\n    }\n\n    .w-full {\n        width: 100%\n    }\n\n    .transform {\n        transform: translate(var(--tw-translate-x), var(--tw-translate-y)) rotate(var(--tw-rotate)) skewX(var(--tw-skew-x)) skewY(var(--tw-skew-y)) scaleX(var(--tw-scale-x)) scaleY(var(--tw-scale-y))\n    }\n\n    .resize {\n        resize: both\n    }\n\n    .rounded {\n        border-radius: 0.25rem\n    }\n\n    .border {\n        border-width: 1px\n    }\n\n    .object-cover {\n        -o-object-fit: cover;\n        object-fit: cover\n    }\n\n    .py-4 {\n        padding-top: 1rem;\n        padding-bottom: 1rem\n    }\n\n    .text-3xl {\n        font-size: 1.875rem;\n        line-height: 2.25rem\n    }\n\n    .text-xl {\n        font-size: 1.25rem;\n        line-height: 1.75rem\n    }\n\n    .uppercase {\n        text-transform: uppercase\n    }\n\n    .lowercase {\n        text-transform: lowercase\n    }\n\n    .italic {\n        font-style: italic\n    }\n\n    .underline {\n        text-decoration-line: underline\n    }\n";
